mapper.select:
select count(*)
from forum
where author_id=#{user_id}

select count(*) from forum
institution_id=#{institutionid}
and author_id=#{userid}
and title like "%"#{code}"%"
and state=1

select f.forum_id,f.author_id,f.institution_id,f.title,f.description,f.create_time,u.username,ic.image,i.institution_name ,u.signature from forum f,institution i,user u,institution_content ic where f.state!=0 and f.author_id=u.user_id and f.institution_id=i.institution_id and i.institution_id=ic.institution_id
and author_id=#{userid}
and title like "%"#{key}"%"
order by create_time desc limit #{start},#{end}
